import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../firebase';
import { signOut } from 'firebase/auth';
import styled from 'styled-components';

const Home = () => {
  const navigate = useNavigate(); // Keep this line

  // ⬇️ Insert this here ⬇️
  const [preferredName, setPreferredName] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        if (auth.currentUser) {
          const idToken = await auth.currentUser.getIdToken();
          const API_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5000";

          const response = await fetch(`${API_URL}/api/auth/me`, {
            headers: {
              'Authorization': `Bearer ${idToken}`
            }
          });

          if (response.ok) {
            const userData = await response.json();
            setPreferredName(userData.preferredName || ''); // Default to empty if null
          }
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);
  // ⬆️ Insert this here ⬆️

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <>
      <HomeWrapper>
        <Frame>
          <LeftBar>DAILY</LeftBar>
          <TopBar>GYM</TopBar>
          <RightBar>SLEEP</RightBar>
          <BottomBar>DIET</BottomBar>

          <CenterContent>
            <h2>Welcome {preferredName ? preferredName : 'Friend'}!</h2>
            <p>You're successfully logged in.</p>
            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </CenterContent>
        </Frame>
      </HomeWrapper>
    </>
  );

};

export default Home;

/* --- STYLES --- */

const HomeWrapper = styled.div`
  position: absolute;
  inset: 0;
  z-index: 0;
  height: 100%;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center; /* Ensures centering */
  text-align: center; /* Keeps text centered */
  padding: 24px 5%;
  background: radial-gradient(125% 125% at 50% 10%, #fff 40%, #63e 100%);
`;


// Frame and Bars styles remain unchanged
const Frame = styled.div`
  position: relative;
  width: 400px;
  height: 400px;
`;

/* Common style base for bars */
const BarBase = styled.div`
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
  font-size: 2rem;
  color: #fff;
`;

const LeftBar = styled(BarBase)`
  top: 80px;
  left: 0;
  width: 60px;
  height: 240px;
  background-color: #ff6600;
  writing-mode: vertical-lr;
  text-orientation: upright;
`;

const TopBar = styled(BarBase)`
  top: 0;
  left: 80px;
  width: 240px;
  height: 60px;
  background-color: #ff0066;
`;

const RightBar = styled(BarBase)`
  top: 80px;
  right: 0;
  width: 60px;
  height: 240px;
  background-color: #4b0082;
  writing-mode: vertical-lr;
  text-orientation: upright;
`;

const BottomBar = styled(BarBase)`
  bottom: 0;
  left: 80px;
  width: 240px;
  height: 60px;
  background-color: #a60064;
`;

const CenterContent = styled.div`
  position: absolute;
  top: 80px;
  bottom: 80px;
  left: 80px;
  right: 80px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  z-index: 10; /* Ensures it's above other elements */

  h1 {
    color: #323232;
    margin-bottom: 20px;
  }

  p {
    margin-bottom: 20px;
  }

  .logout-btn {
    margin-top: 10px;
    padding: 10px 20px;
    border: 2px solid #323232;
    border-radius: 5px;
    background-color: white;
    cursor: pointer;
    font-weight: 600;
    box-shadow: 4px 4px #323232;
    transition: all 0.2s ease;
    position: relative;
    z-index: 9999;
    pointer-events: auto !important;

    &:hover {
      transform: translate(2px, 2px);
      box-shadow: 2px 2px #323232;
    }
  }
`;
