import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import { auth } from "../firebase";

const Exercise = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [selectedSplit, setSelectedSplit] = useState("Select Workout Split");
  const [workoutPlan, setWorkoutPlan] = useState({});
  const [loading, setLoading] = useState(true);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  // ‚úÖ Workout split options
  const workoutSplits = [
    { id: "total_body", label: "Total Body Split" },
    { id: "upper_lower", label: "Upper vs. Lower Split" },
    { id: "push_pull_legs", label: "Push vs. Pull vs. Legs Split" },
    { id: "bro_split", label: "Bro Split" }
  ];

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const idToken = await auth.currentUser?.getIdToken(true);
        if (!idToken) return;

        const API_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5000";
        const res = await fetch(`${API_URL}/api/auth/me`, {
          method: "GET",
          headers: {
            "Authorization": `Bearer ${idToken}`,
            "Content-Type": "application/json"
          }
        });

        const data = await res.json();
        setUserData(data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchUserData();
  }, []);

  const fetchWorkoutPlan = async () => {
    if (selectedSplit === "Select Workout Split") {
      alert("Please select a workout split.");
      return;
    }

    try {
      const API_URL = "http://localhost:5000";
      console.log(`üì° Fetching workout plan from: ${API_URL}/api/full_recommendation?split_type=${selectedSplit}`);

      const response = await fetch(`${API_URL}/api/full_recommendation?split_type=${selectedSplit}`);

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`‚ùå Failed to fetch workout plan. Server response: ${errorText}`);
        throw new Error(`Failed to fetch workout plan: ${errorText}`);
      }

      const data = await response.json();
      console.log("‚úÖ Workout plan received:", data);

      setWorkoutPlan(data.workout_plan || {});
    } catch (error) {
      console.error("üö® Error fetching workout plan:", error.message);
    }
  };

  if (loading) return <p>Loading user data...</p>;

  return (
    <ExerciseWrapper>
      <Header>
        <span>Exercise</span>
        <HomeButton onClick={() => navigate("/")}>Home</HomeButton>
      </Header>
      <Content>
        <h1>Create Your Workout Plan</h1>

        {/* ‚úÖ Custom Dropdown */}
        <DropdownContainer>
          <DropdownSelected onClick={() => setDropdownOpen(!dropdownOpen)}>
            {selectedSplit}
            <DropdownArrow open={dropdownOpen} />
          </DropdownSelected>
          {dropdownOpen && (
            <DropdownOptions>
              {workoutSplits.map((split) => (
                <DropdownOption
                  key={split.id}
                  onClick={() => {
                    setSelectedSplit(split.id);
                    setDropdownOpen(false);
                  }}
                >
                  {split.label}
                </DropdownOption>
              ))}
            </DropdownOptions>
          )}
        </DropdownContainer>

        <GenerateButton onClick={fetchWorkoutPlan}>Generate Plan</GenerateButton>

        {workoutPlan && Object.keys(workoutPlan).length > 0 ? (
          <WorkoutPlanContainer>
            <h3>Your Workout Plan:</h3>
            <WorkoutRow>
              {Object.entries(workoutPlan).map(([category, exercises]) => (
                <WorkoutColumn key={category}>
                  <h4>{category.toUpperCase()}</h4>
                  <ul>
                    {exercises.map((exercise, index) => (
                      <li key={index}>{exercise}</li>
                    ))}
                  </ul>
                </WorkoutColumn>
              ))}
            </WorkoutRow>
          </WorkoutPlanContainer>
        ) : (
          <p>No workout plan available yet. Please generate one.</p>
        )}
      </Content>
    </ExerciseWrapper>
  );
};

export default Exercise;

/* --- STYLES --- */

const ExerciseWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  width: 100%;
  background: radial-gradient(125% 125% at 50% 10%, rgb(217, 39, 39) 40%, #000 100%);
  color: white;
  text-align: center;
  padding: 20px;
`;

const Header = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 60px;
  background-color: #fff;
  color: black;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  font-weight: bold;
  z-index: 1000;
  box-shadow: 0 4px 6px rgb(201, 80, 169);
`;

const HomeButton = styled.button`
  position: absolute;
  right: 20px;
  top: 50%;
  transform: translateY(-50%);
  padding: 8px 16px;
  font-size: 14px;
  font-weight: bold;
  border: .25em solid rgb(217, 176, 255);
  background-color: #fff;
  color: rgb(217, 176, 255);
  border-radius: 1em;
  cursor: pointer;
  transition: all 0.3s;
`;

const Content = styled.div`
  margin-top: 100px;
  text-align: center;
  width: 80%;
  max-width: 1000px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

/* ‚úÖ Custom Dropdown Styles */
const DropdownContainer = styled.div`
  position: relative;
  width: 250px;
`;

const DropdownSelected = styled.div`
  background-color: #2a2f3b;
  padding: 10px;
  border-radius: 5px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  cursor: pointer;
`;

const DropdownArrow = styled.div`
  width: 10px;
  height: 10px;
  border-left: 2px solid white;
  border-bottom: 2px solid white;
  transform: ${({ open }) => (open ? "rotate(225deg)" : "rotate(45deg)")};
  transition: transform 0.3s;
`;

const DropdownOptions = styled.div`
  position: absolute;
  width: 100%;
  background-color: #2a2f3b;
  border-radius: 5px;
  margin-top: 5px;
  z-index: 10;
`;

const DropdownOption = styled.div`
  padding: 10px;
  cursor: pointer;
  &:hover {
    background-color: #323741;
  }
`;

const GenerateButton = styled.button`
  margin-top: 15px;
  padding: 10px 20px;
  font-size: 16px;
  background-color: #ff4081;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const WorkoutPlanContainer = styled.div`
  margin-top: 20px;
  padding: 20px;
  background-color: rgba(255, 255, 255, 0.1);
  border-radius: 10px;
  width: 90%;
  max-width: 1200px;
`;

const WorkoutRow = styled.div`
  display: flex;
  justify-content: center;
  gap: 40px;
  width: 100%;
  max-width: 1000px;
`;

const WorkoutColumn = styled.div`
  flex: 1;
  max-width: 300px;
  text-align: center;
`;
