// src/components/Home.js

import React from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../firebase';
import { signOut } from 'firebase/auth';
import styled from 'styled-components';

const Home = () => {
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <HomeWrapper>
      <Frame>
        <LeftBar>DAILY</LeftBar>
        <TopBar>GYM</TopBar>
        <RightBar>SLEEP</RightBar>
        <BottomBar>DIET</BottomBar>

        <CenterContent>
          <h1>Welcome to Your Fitness Journey!</h1>
          <p>You're successfully logged in.</p>
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </CenterContent>
      </Frame>
    </HomeWrapper>
  );
};

export default Home;

/* --- STYLES --- */

const HomeWrapper = styled.div`
  /* Full viewport height, centers the Frame horizontally/vertically */
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  background-color: #f5f5f5;
`;

const Frame = styled.div`
  position: relative;
  width: 400px;
  height: 400px;
`;

/* Common style base for bars */
const BarBase = styled.div`
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
  font-size: 1.2rem;
  color: #000;
`;

/* Left Bar (vertical), shifted down to create staggered effect */
const LeftBar = styled(BarBase)`
  top: 100px;
  left: 0;
  width: 60px;
  height: 200px; /* Shortened to create space at top and bottom */
  background-color: #ff6600; /* Orange */
  writing-mode: vertical-lr;
  text-orientation: upright;
`;

/* Top Bar (horizontal), offset to the right */
const TopBar = styled(BarBase)`
  top: 0;
  left: 100px;
  width: 200px; /* Shortened to create space on left and right */
  height: 60px;
  background-color: #ff0066; /* Pink */
`;

/* Right Bar (vertical), shifted up to create staggered effect */
const RightBar = styled(BarBase)`
  top: 100px;
  right: 0;
  width: 60px;
  height: 200px; /* Shortened to create space at top and bottom */
  background-color: #4b0082; /* Purple */
  writing-mode: vertical-lr;
  text-orientation: upright;
`;

/* Bottom Bar (horizontal), offset to the left */
const BottomBar = styled(BarBase)`
  bottom: 0;
  left: 100px;
  width: 200px; /* Shortened to create space on left and right */
  height: 60px;
  background-color: #a60064; /* Maroon/Magenta */
`;

/* Center content area */
const CenterContent = styled.div`
  position: absolute;
  top: 100px;
  bottom: 100px;
  left: 100px;
  right: 100px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;

  h1 {
    color: #323232;
    margin-bottom: 20px;
  }

  p {
    margin-bottom: 20px;
  }

  .logout-btn {
    margin-top: 10px;
    padding: 10px 20px;
    border: 2px solid #323232;
    border-radius: 5px;
    background-color: white;
    cursor: pointer;
    font-weight: 600;
    box-shadow: 4px 4px #323232;
    transition: all 0.2s ease;

    &:hover {
      transform: translate(2px, 2px);
      box-shadow: 2px 2px #323232;
    }
  }
`;
