import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { Pie } from 'react-chartjs-2';
import Chart from 'chart.js/auto';

const SPOONACULAR_API_KEY = 'YOUR_SPOONACULAR_API_KEY';

const Diet = () => {
  const navigate = useNavigate();
  const [ingredients, setIngredients] = useState([]);
  const [input, setInput] = useState('');
  const [nutritionData, setNutritionData] = useState(null);
  const [missingNutrients, setMissingNutrients] = useState([]);
  const [recipeSuggestions, setRecipeSuggestions] = useState([]);

  // Add ingredient
  const addIngredient = () => {
    if (input.trim() !== '') {
      setIngredients([...ingredients, input.trim()]);
      setInput('');
    }
  };

  // Fetch nutritional data and recipes from Spoonacular
  const fetchNutritionAndRecipes = async () => {
    if (ingredients.length === 0) return;

    const nutritionResponse = await fetch(`https://api.spoonacular.com/recipes/guessNutrition?title=${ingredients.join(',')}&apiKey=${SPOONACULAR_API_KEY}`);
    const nutritionData = await nutritionResponse.json();
    setNutritionData({
      calories: nutritionData.calories?.value || 0,
      protein: nutritionData.protein?.value || 0,
      carbs: nutritionData.carbs?.value || 0,
      fats: nutritionData.fat?.value || 0,
    });

    const recipeResponse = await fetch(`https://api.spoonacular.com/recipes/findByIngredients?ingredients=${ingredients.join(',')}&number=5&apiKey=${SPOONACULAR_API_KEY}`);
    const recipes = await recipeResponse.json();

    const detailedRecipes = await Promise.all(
      recipes.map(async (recipe) => {
        const detailsResponse = await fetch(`https://api.spoonacular.com/recipes/${recipe.id}/information?apiKey=${SPOONACULAR_API_KEY}`);
        const details = await detailsResponse.json();
        return { title: details.title, sourceUrl: details.sourceUrl };
      })
    );

    setRecipeSuggestions(detailedRecipes);
  };

  return (
    <Wrapper>
      <h1 className="title">Manage Your Nutrition</h1>
      <p>Input your favorite ingredients or available foods:</p>
      <InputSection>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Enter an ingredient"
          className="flip-card__input"
        />
        <button onClick={addIngredient} className="flip-card__btn">Add</button>
      </InputSection>
      <IngredientList>
        {ingredients.map((ing, idx) => (
          <li key={idx}>{ing}</li>
        ))}
      </IngredientList>
      <button onClick={fetchNutritionAndRecipes} className="flip-card__btn">Analyze Diet & Get Recipes</button>

      {nutritionData && (
        <NutritionSection>
          <h2 className="title">Nutritional Summary</h2>
          <Pie
            data={{
              labels: ['Protein', 'Carbs', 'Fats'],
              datasets: [{
                data: [nutritionData.protein, nutritionData.carbs, nutritionData.fats],
                backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
              }],
            }}
          />
          <h3>Missing Nutrients</h3>
          <ul>
            {missingNutrients.length > 0 ? missingNutrients.map((nutrient, idx) => (
              <li key={idx}>{nutrient}</li>
            )) : <p>You're getting all required nutrients!</p>}
          </ul>
        </NutritionSection>
      )}

      {recipeSuggestions.length > 0 && (
        <RecipeSection>
          <h2 className="title">Recipe Suggestions</h2>
          <ul>
            {recipeSuggestions.map((recipe, idx) => (
              <li key={idx}>
                <a href={recipe.sourceUrl} target="_blank" rel="noopener noreferrer">{recipe.title}</a>
              </li>
            ))}
          </ul>
        </RecipeSection>
      )}
    </Wrapper>
  );
};

export default Diet;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  padding: 20px;
  background-color: #f5f5f5;
  text-align: center;
`;

const InputSection = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
`;

const IngredientList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin-bottom: 20px;

  li {
    font-size: 18px;
    padding: 5px;
  }
`;

const NutritionSection = styled.div`
  margin-top: 20px;
`;

const RecipeSection = styled.div`
  margin-top: 20px;
`;

const Button = styled.button`
  margin: 20px 0;
  width: 120px;
  height: 40px;
  border-radius: 5px;
  border: 2px solid var(--main-color);
  background-color: var(--bg-color);
  box-shadow: 4px 4px var(--main-color);
  font-size: 17px;
  font-weight: 600;
  color: var(--font-color);
  cursor: pointer;
  transition: all 0.3s;
  &:hover {
    transform: translate(2px, 2px);
    box-shadow: 2px 2px var(--main-color);
  }
`;
