import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../firebase';

const Exercise = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [selectedSplit, setSelectedSplit] = useState('');
  const [workoutPlan, setWorkoutPlan] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        if (!auth.currentUser) {
          console.warn("‚ö†Ô∏è No authenticated user found. Redirecting...");
          navigate('/');
          return;
        }

        const idToken = await auth.currentUser.getIdToken();
        const API_URL = "http://localhost:5000"; // ‚úÖ Force local backend

        const response = await fetch(`${API_URL}/api/auth/me`, {
          headers: {
            'Authorization': `Bearer ${idToken}`
          }
        });

        if (response.ok) {
          const userData = await response.json();
          setUserData(userData);
        } else {
          console.error("‚ùå Failed to fetch user data, redirecting to login...");
          navigate('/');
        }
      } catch (error) {
        console.error('üö® Error fetching user data:', error);
        navigate('/');
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [navigate]);

  const fetchWorkoutPlan = async () => {
    if (!selectedSplit) {
      alert("Please select a workout split.");
      return;
    }

    try {
      const API_URL = "http://localhost:5000"; // ‚úÖ Ensure local backend
      const response = await fetch(`${API_URL}/api/full_recommendation?split_type=${selectedSplit}`);

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`‚ùå Failed to fetch workout plan. Server response: ${errorText}`);
      }

      const data = await response.json();
      setWorkoutPlan(data.workout_plan);
    } catch (error) {
      console.error("üö® Error fetching workout plan:", error.message);
    }
  };

  return {
    userData,
    selectedSplit,
    setSelectedSplit,
    workoutPlan,
    fetchWorkoutPlan,
    loading
  };
};

export default Exercise;


const ExerciseWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background: radial-gradient(125% 125% at 50% 10%,rgb(217, 39, 39) 40%, #000 100%);
  color: white;
  text-align: center;
`;

const Header = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 60px;
  background-color: #fff;
  color: black;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  font-weight: bold;
  z-index: 1000;
  box-shadow: 0 4px 6px rgb(201, 80, 169);

  span {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
  }
`;

const HomeButton = styled.button`
  position: absolute;
  right: 20px;
  top: 50%;
  transform: translateY(-50%);
  padding: 8px 16px;
  font-size: 14px;
  font-weight: bold;
  border: .25em solid rgb(217, 176, 255);
  background-color: #fff;
  color: rgb(217, 176, 255);
  border-radius: 1em;
  cursor: pointer;
  transition: all 0.3s;
  box-shadow: 0 0 1em .25em rgb(217, 176, 255),
              0 0 4em 1em rgba(191, 123, 255, 0.5),
              inset 0 0 .75em .25em rgb(217, 176, 255);
  text-shadow: 0 0 .5em rgb(217, 176, 255);

  &:hover {
    background-color: rgb(217, 176, 255);
    color: #222;
  }
`;

const Content = styled.div`
  margin-top: 80px;
  text-align: center;
  h1 {
    font-size: 36px;
    margin-bottom: 20px;
  }
  p {
    font-size: 18px;
  }
  ul {
    list-style: none;
    padding: 0;
  }
  li {
    font-size: 20px;
    margin: 10px 0;
  }
`;
