import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../firebase';
import { signOut } from 'firebase/auth';
import styled from 'styled-components';

const Home = () => {
  const navigate = useNavigate();
  const [preferredName, setPreferredName] = useState(''); // State to store name

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        if (auth.currentUser) {
          const idToken = await auth.currentUser.getIdToken();
          const API_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5000";

          const response = await fetch(`${API_URL}/api/auth/me`, {
            headers: {
              'Authorization': `Bearer ${idToken}`
            }
          });

          if (response.ok) {
            const userData = await response.json();
            setPreferredName(userData.preferredName || ''); // Default to empty if null
          }
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <HomeWrapper>
      <Frame>
        <LeftBar>DAILY</LeftBar>
        <TopBar>GYM</TopBar>
        <RightBar>SLEEP</RightBar>
        <BottomBar>DIET</BottomBar>

        <CenterContent>
          <h1>Welcome {preferredName ? preferredName : 'Friend'}!</h1>
          <p>You're successfully logged in.</p>
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </CenterContent>
      </Frame>
    </HomeWrapper>
  );
};

export default Home;

/* --- STYLES --- */
const HomeWrapper = styled.div`
  position: relative;
  height: 100vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: 24px 5%;
  background: radial-gradient(125% 125% at 50% 10%, #fff 40%, #63e 100%);
  z-index: 0;
`;

const CenterContent = styled.div`
  position: relative;
  z-index: 10;
  text-align: center;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  pointer-events: auto;

  h1 {
    color: #323232;
    margin-bottom: 20px;
  }

  p {
    margin-bottom: 20px;
  }

  .logout-btn {
    margin-top: 10px;
    padding: 10px 20px;
    border: 2px solid #323232;
    border-radius: 5px;
    background-color: white;
    cursor: pointer;
    font-weight: 600;
    box-shadow: 4px 4px #323232;
    transition: all 0.2s ease;
    position: relative;
    z-index: 9999;
    pointer-events: auto !important;

    &:hover {
      transform: translate(2px, 2px);
      box-shadow: 2px 2px #323232;
    }
  }
`;
