import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../firebase';

const glowEffect = `
  border: .25em solid var(--glow-color);
  padding: 1em;
  color: color-mix(in srgb, var(--glow-color) 70%, white);
  font-size: 20px;
  font-weight: bold;
  background-color: var(--btn-color);
  border-radius: 1em;
  outline: none;
  box-shadow: 0 0 1em .25em var(--glow-color),
        0 0 4em 1em var(--glow-spread-color),
        inset 0 0 .75em .25em var(--glow-color);
  text-shadow: 0 0 .5em var(--glow-color);
  position: relative;
  transition: all 0.3s;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;

  &:after {
    pointer-events: none;
    content: "";
    position: absolute;
    top: 120%;
    left: 0;
    height: 100%;
    width: 100%;
    background-color: var(--glow-spread-color);
    filter: blur(2em);
    opacity: .7;
    transform: perspective(1.5em) rotateX(35deg) scale(1, .6);
  }

  &:hover {
    color: color-mix(in srgb, var(--glow-color) 70%, white); /* ✅ Lightens text color */

    background-color: var(--glow-color);
    box-shadow: 0 0 1em .25em var(--glow-color),
          0 0 4em 2em var(--glow-spread-color),
          inset 0 0 .75em .25em var(--glow-color);
  }

  &:active {
    box-shadow: 0 0 0.6em .25em var(--glow-color),
          0 0 2.5em 2em var(--glow-spread-color),
          inset 0 0 .5em .25em var(--glow-color);
  }
`;

const Exercise = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [selectedSplit, setSelectedSplit] = useState('');
  const [workoutPlan, setWorkoutPlan] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        if (!auth.currentUser) {
          console.warn("⚠️ No authenticated user found. Redirecting...");
          navigate('/');
          return;
        }

        const idToken = await auth.currentUser.getIdToken();
        const API_URL = "http://localhost:5000"; // ✅ Force local backend

        const response = await fetch(`${API_URL}/api/auth/me`, {
          headers: {
            'Authorization': `Bearer ${idToken}`
          }
        });

        if (response.ok) {
          const userData = await response.json();
          setUserData(userData);
        } else {
          console.error("❌ Failed to fetch user data, redirecting to login...");
          navigate('/');
        }
      } catch (error) {
        console.error('🚨 Error fetching user data:', error);
        navigate('/');
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [navigate]);

  const fetchWorkoutPlan = async () => {
    if (!selectedSplit) {
      alert("Please select a workout split.");
      return;
    }

    try {
      const API_URL = "http://localhost:5000"; // ✅ Ensure local backend
      const response = await fetch(`${API_URL}/api/full_recommendation?split_type=${selectedSplit}`);

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`❌ Failed to fetch workout plan. Server response: ${errorText}`);
      }

      const data = await response.json();
      setWorkoutPlan(data.workout_plan);
    } catch (error) {
      console.error("🚨 Error fetching workout plan:", error.message);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Create Your Workout Plan</h2>
      <label>Select Workout Split:</label>
      <select value={selectedSplit} onChange={(e) => setSelectedSplit(e.target.value)}>
        <option value="">Select Split</option>
        <option value="total_body">Total Body Split</option>
        <option value="upper_lower">Upper vs. Lower Split</option>
        <option value="push_pull_legs">Push vs. Pull vs. Legs Split</option>
        <option value="bro_split">Bro Split</option>
      </select>
      <button onClick={fetchWorkoutPlan}>Generate Plan</button>

      {workoutPlan && (
        <div>
          <h3>Your Workout Plan:</h3>
          {Object.entries(workoutPlan).map(([day, exercises]) => (
            <div key={day}>
              <h4>{day.toUpperCase()}</h4>
              <ul>
                {exercises.map((exercise, index) => (
                  <li key={index}>{exercise}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Exercise;


