import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { auth } from '../firebase';

const API_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5000";

const Exercise = () => {
  const navigate = useNavigate();
  const [exercisePlan, setExercisePlan] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      if (!auth.currentUser) return;

      try {
        const idToken = await auth.currentUser.getIdToken(true);
        const response = await fetch(`${API_URL}/api/auth/me`, {
          headers: { 'Authorization': `Bearer ${idToken}` }
        });

        if (!response.ok) throw new Error('Failed to fetch user data');

        const userData = await response.json();
        setExercisePlan(generateExercisePlan(userData));
      } catch (error) {
        console.error('Error fetching user data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  const generateExercisePlan = (user) => {
    const { workoutFrequency, experienceLevel, equipmentAccess, fitnessGoals } = user;
    let plan = [];

    if (workoutFrequency.includes('1-2')) {
      plan = ['Full Body (2x per week)'];
    } else if (workoutFrequency.includes('3-4')) {
      plan = ['Upper Body', 'Lower Body', 'Full Body (optional)'];
    } else if (workoutFrequency.includes('5-6')) {
      plan = ['Push', 'Pull', 'Legs', 'Full Body'];
    } else {
      plan = ['Push', 'Pull', 'Legs', 'Arms & Core', 'Full Body'];
    }

    if (equipmentAccess.includes('None')) {
      plan = plan.map(day => `${day} (Bodyweight Exercises)`);
    } else if (equipmentAccess.includes('Dumbbells')) {
      plan = plan.map(day => `${day} (Dumbbell Workouts)`);
    }

    return plan;
  };

  return (
    <ExerciseWrapper>
      <Header>
        <span>Exercise Plan</span>
        <HomeButton onClick={() => navigate('/')}>Home</HomeButton>
      </Header>
      <Content>
        {loading ? (
          <p>Loading your personalized plan...</p>
        ) : (
          <>
            <h1>Your Weekly Exercise Split</h1>
            <ul>
              {exercisePlan.map((day, index) => (
                <li key={index}>{day}</li>
              ))}
            </ul>
          </>
        )}
      </Content>
    </ExerciseWrapper>
  );
};

export default Exercise;

const ExerciseWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background: radial-gradient(125% 125% at 50% 10%,rgb(217, 39, 39) 40%, #000 100%);
  color: white;
  text-align: center;
`;

const Header = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 60px;
  background-color: #fff;
  color: black;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  font-weight: bold;
  z-index: 1000;
  box-shadow: 0 4px 6px rgb(201, 80, 169);

  span {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
  }
`;

const HomeButton = styled.button`
  position: absolute;
  right: 20px;
  top: 50%;
  transform: translateY(-50%);
  padding: 8px 16px;
  font-size: 14px;
  font-weight: bold;
  border: .25em solid rgb(217, 176, 255);
  background-color: #fff;
  color: rgb(217, 176, 255);
  border-radius: 1em;
  cursor: pointer;
  transition: all 0.3s;
  box-shadow: 0 0 1em .25em rgb(217, 176, 255),
              0 0 4em 1em rgba(191, 123, 255, 0.5),
              inset 0 0 .75em .25em rgb(217, 176, 255);
  text-shadow: 0 0 .5em rgb(217, 176, 255);

  &:hover {
    background-color: rgb(217, 176, 255);
    color: #222;
  }
`;

const Content = styled.div`
  margin-top: 80px;
  text-align: center;
  h1 {
    font-size: 36px;
    margin-bottom: 20px;
  }
  p {
    font-size: 18px;
  }
  ul {
    list-style: none;
    padding: 0;
  }
  li {
    font-size: 20px;
    margin: 10px 0;
  }
`;
